function toggleMenu(e=!1){const t=document.querySelector(".nav-container");if(!t)return;const n=!e&&!t.classList.contains("active");t.classList.toggle("active",n),document.querySelectorAll(".list").forEach((e=>{e.style.transform=n?window.innerWidth<768?"translateX(135px)":"translateX(138px)":"translateX(0)",e.classList.toggle("shifted",n)}))}document.querySelectorAll(".nav-btn").forEach((e=>{e.addEventListener("click",(()=>toggleMenu(!0)))}));const menuToggle=document.querySelector(".menu-toggle"),logo=document.querySelector(".logo");menuToggle.addEventListener("click",(function(){this.classList.toggle("active"),logo.style.display=this.classList.contains("active")?"flex":"none"}));